import { useState, useEffect } from "react";
import axios from "axios";
import {
  MDBTable,
  MDBTableHead,
  MDBTableBody,
  MDBInput,
} from "mdb-react-ui-kit";
import { Modal, ModalBody, ModalHeader } from "reactstrap";
import axiosCall from "../../AxiosCall";

const NewServices = () => {
  const [data, setData] = useState([]);
  const [message, setMessage] = useState("");
  const [modal, setModal] = useState(false);
  const [comment, setComment] = useState("High Price");
  const toggle = () => setModal(!modal);
  useEffect(() => {
    const fetchData = async () => {
      try {
        const status = "Pending";
        const response = await axios.get(
          `http://localhost:8000/admin/getServices/${status}`,
          { withCredentials: true }
        );
        setData(response.data);
        console.log(response.data);
        console.log("data", response.data[0]?.sellerId?.name);
      } catch (error) {
        setMessage(error.response?.data?.error);
      }
    };

    fetchData();
  }, []);

  const approveService = async (e) => {
    console.log(e.target.id, "id is");
    try {
      const id = e.target.id;
      const response = await axiosCall("PUT","admin/approveService",{},)
      await axios.put(
        `http://localhost:8000/admin/approveService/${id}`,
        {},
        {
          withCredentials: true,
        }
      );
      if (response.status === 200) {
        setData((prevData) =>
          prevData.filter((item) => item._id !== e.target.id)
        );
      }
      // Update UI logic here
    } catch (error) {
      console.error("Error Approving Service:", error);
      setMessage("Error Approving Service");
    } finally {
      setTimeout(function () {
        setMessage("");
      }, 1500);
    }
  };

  const rejectService = async (e) => {
    try {
      const id = e.target.id;
      console.log("in reject service", id, comment);
      const response = await axiosCall("PUT","admin/rejectService",{comment:comment},id)
      //  axios.put(
      //   `http://localhost:8000/admin/rejectService/${id}`,
      //   JSON.stringify({ comment: comment }),
      //   {
      //     headers: {
      //       "Content-Type": "application/json",
      //     },
      //     withCredentials: true,
      //   }
      // );
      if (response.status === 200) {
        setData((prevData) =>
          prevData.filter((item) => item._id !== e.target.id)
        );
      }
      // Update UI logic here
    } catch (error) {
      console.error("Error Approving Service:", error);
      setMessage("Error Approving Service");
    } finally {
      setTimeout(function () {
        setMessage("");
      }, 1500);
    }
  };

  return (
    <div>
      <p className="text-center text-danger">{message}</p>
      <div className="d-flex justify-content-center align-items-center">
        <MDBTable className="w-75">
          <MDBTableHead>
            <tr>
              <th scope="col">Sr. No</th>
              <th scope="col">Title</th>
              <th scope="col">Location</th>
              <th scope="col">Price</th>
              <th scope="col">Status</th>
              <th scope="col">Description</th>
              <th scope="col">Seller Name</th>
              <th scope="col">Category</th>
              <th scope="col">Approve</th>
              <th scope="col">Reject</th>
            </tr>
          </MDBTableHead>
          <MDBTableBody>
            {data.map((item, index) => (
              <tr key={item._id}>
                <td>
                  <div className="d-flex align-items-center">
                    <div className="ms-3">
                      <p key={item._id} className="fw-bold mb-1">
                        {index}
                      </p>
                    </div>
                  </div>
                </td>
                <td>
                  <p key={item._id} className="fw-normal mb-1">
                    {item.title}
                  </p>
                </td>
                <td>
                  <p key={item._id} className="fw-normal mb-1">
                    {item.location}
                  </p>
                </td>
                <td>
                  <p key={item._id} className="fw-normal mb-1">
                    {item.price}
                  </p>
                </td>
                <td>
                  <p key={item._id} className="fw-normal mb-1">
                    {item.status}
                  </p>
                </td>
                <td>
                  <p key={item._id} className="fw-normal mb-1">
                    {item.description}
                  </p>
                </td>
                <td>
                  <p key={item._id} className="fw-normal mb-1">
                    {item.sellerId?.name}
                  </p>
                </td>
                <td>
                  <p key={item._id} className="fw-normal mb-1">
                    {item.categoryId?.name}
                  </p>
                </td>
                <td>
                  <button
                    id={item._id}
                    className="me-1 btn btn-success"
                    color="danger"
                    size="sm"
                    onClick={approveService}
                    // disabled={isDeleting}
                  >
                    Approve
                  </button>
                </td>
                <td>
                  <button
                    id={item._id}
                    className="me-1 btn btn-danger"
                    color="danger"
                    size="sm"
                    onClick={rejectService}
                    // disabled={isDeleting}
                  >
                    Reject
                  </button>
                </td>
              </tr>
            ))}
          </MDBTableBody>
        </MDBTable>
      </div>
      <Modal isOpen={modal} toggle={toggle}>
        <ModalHeader toggle={toggle}>Add Category</ModalHeader>
        <ModalBody>
          <form onSubmit={rejectService}>
            <MDBInput
              wrapperClass="mb-4"
              label="Category Name"
              type="text"
              size="lg"
              required
              value={comment}
              onChange={(e) => {
                setComment(e.target.value);
              }}
            />
            <div className="d-flex justify-content-end">
              <button
                className="btn btn-primary "
                onClick={toggle}
                type="submit"
              >
                Add Category
              </button>
            </div>
          </form>
        </ModalBody>
      </Modal>
    </div>
  );
};
export default NewServices;
