import React, { useState, useEffect } from "react";
import axios from "axios";
import { MDBTable, MDBTableHead, MDBTableBody } from "mdb-react-ui-kit";
import { Modal, ModalHeader } from "reactstrap";

const Categories = () => {
  const [data, setData] = useState([]);
  const [message, setMessage] = useState("");
  const [isDeleting, setIsDeleting] = useState(false);
  const [IsFormOpen, setIsFormOpen] = useState(false);
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          "http://localhost:8000/admin/getCategories",
          { withCredentials: true }
        );
        setData(response.data);
        console.log("categories json data", response.data);
      } catch (error) {
        setMessage(error.response?.data?.error);

        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  const handleDelete = async (e) => {
    e.preventDefault();
    console.log("id", e.target.id);
    try {
      console.log("id", e.target.id);
      await axios.delete(
        `http://localhost:8000/admin/deleteCategory/${e.target.id}`,
        { withCredentials: true }
      );
      setIsDeleting(true);
      setMessage("Data deleted successfully!");
      setData((prevData) =>
        prevData.filter((item) => item._id !== e.target.id)
      );
    } catch (error) {
      console.error("Error deleting data:", error);
    } finally {
      setIsDeleting(false);
      setTimeout(function () {
        setMessage("");
      }, 2000);
    }
  };

  return (
    <div>
      <Modal
        size="lg"
        isOpen={IsFormOpen}
        toggle={() => {
          setIsFormOpen(false);
        }}
      >
        <ModalHeader
          toggle={() => {
            setIsFormOpen(false);
          }}
        >
          Popup
        </ModalHeader>
      </Modal>
      <p style={{ textAlign: "center" }}>{message}</p>
      <div style={{ display: "flex", justifyContent: "flex-end" }}>
        <a href="/viewCategories">
          <button
            className="btn btn-success"
            size="sm"
            onClick={() => {
              setIsFormOpen(true);
            }}
          >
            + Add Category
          </button>
        </a>
      </div>
      <MDBTable className="m-4" style={{ width: "80%" }}>
        <MDBTableHead>
          <tr>
            <th scope="col">Sr. No</th>
            <th scope="col">Title</th>
            <th scope="col">Delete</th>
          </tr>
        </MDBTableHead>
        <MDBTableBody>
          {data.map((item, index) => (
            <tr key={item._id}>
              <td>
                <div className="d-flex align-items-center">
                  <div className="ms-3">
                    <p key={item._id} className="fw-bold mb-1">
                      {index}
                    </p>
                  </div>
                </div>
              </td>
              <td>
                <p key={item._id} className="fw-normal mb-1">
                  {item.name}
                </p>
              </td>
              <td>
                <button
                  id={item._id}
                  className="me-1 btn btn-danger"
                  color="danger"
                  size="sm"
                  onClick={handleDelete}
                  disabled={isDeleting}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </MDBTableBody>
      </MDBTable>
    </div>
  );
};
export default Categories;
