import { useState, useEffect } from "react";
import axios from "axios";
import {
  MDBBtn,
  MDBTable,
  MDBTableHead,
  MDBTableBody,
  MDBModal,
  MDBModalHeader,
  MDBModalBody,
  MDBInput,
  MDBModalFooter,
} from "mdb-react-ui-kit";

const Categories = () => {
  const [data, setData] = useState([]);
  const [message, setMessage] = useState("");
  const [isDeleting, setIsDeleting] = useState(false);
  const [isAdding, setIsAdding] = useState(false);
  const [categoryName, setCategoryName] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          "http://localhost:8000/admin/getCategories",
          { withCredentials: true }
        );
        setData(response.data);
      } catch (error) {
        setMessage(error.response?.data?.error);
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, [isDeleting]);

  const handleDelete = async (e) => {
    e.preventDefault();
    try {
      await axios.delete(
        `http://localhost:8000/admin/deleteCategory/${e.target.id}`,
        { withCredentials: true }
      );
      setIsDeleting(true);
      setMessage("Data deleted successfully!");
    } catch (error) {
      console.error("Error deleting data:", error);
    } finally {
      setIsDeleting(false);
      setTimeout(function () {
        setMessage("");
      }, 2000);
    }
  };

  const handleAdd = () => {
    setIsAdding(true);
  };

  const handleAddCategory = async () => {
    try {
      const response = await axios.post(
        "http://localhost:8000/admin/addCategory",
        { name: categoryName },
        { withCredentials: true }
      );
      setIsAdding(false);
      setData([...data, response.data]);
      setMessage("Category added successfully!");
    } catch (error) {
      console.error("Error adding category:", error);
    } finally {
      setTimeout(function () {
        setMessage("");
      }, 2000);
    }
  };

  return (
    <div>
      <p style={{ textAlign: "center" }}>{message}</p>
      <div style={{ display: "flex", justifyContent: "flex-end" }}>
        <button className="me-1 btn btn-success" size="sm" onClick={handleAdd}>
         + Add Category
        </button>
      </div>
      <MDBTable className="m-4" style={{ width: "80%" }}>
        <MDBTableHead>
          <tr>
            <th scope="col">Sr. No</th>
            <th scope="col">Title</th>
            <th scope="col">Delete</th>
          </tr>
        </MDBTableHead>
        <MDBTableBody>
          {data.map((item, index) => (
            <tr key={item._id}>
              <td>
                <div className="d-flex align-items-center">
                  <div className="ms-3">
                    <p key={item._id} className="fw-bold mb-1">
                      {index}
                    </p>
                  </div>
                </div>
              </td>
              <td>
                <p key={item._id} className="fw-normal mb-1">
                  {item.name}
                </p>
              </td>
              <td>
                <button
                  id={item._id}
                  className="me-1 btn btn-danger"
                  color="danger"
                  size="sm"
                  onClick={handleDelete}
                  disabled={isDeleting}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </MDBTableBody>
      </MDBTable>
      <MDBModal isOpen={isAdding} toggle={() => setIsAdding(false)}>
        <MDBModalHeader>Add Category</MDBModalHeader>
        <MDBModalBody>
          <MDBInput
            label="Name"
            value={categoryName}
            onChange={(e) => setCategoryName(e.target.value)}
          />
        </MDBModalBody>
        <MDBModalFooter>
          <MDBBtn color="secondary" onClick={() => setIsAdding(false)}>
            Cancel
          </MDBBtn>
          <MDBBtn color="primary" onClick={handleAddCategory}>
            Add Category
          </MDBBtn>
        </MDBModalFooter>
      </MDBModal>
    </div>
  );
};

export default Categories;
